import {motion} from 'framer-motion';
import style from './animated.module.css';

export const AnimatedTitle = () => {
    const text = 'Animated Text' // This would normally be passed into this component as a prop!

    const changeText = text.split(" ")

    console.log(changeText)

    const containerAnimation = {
        hidden: {
            opacity: 0,
        },
        visible: (custom: number, i: number) => ({
            opacity: 1,
            transition: {
                delayChildren: i * 0.04,
                staggerChildren: 0.12,
                duration: 0.9,
                delay: custom * 0.25
            },
        })
    }


    const child = {
        visible: {
            opacity: 1,
            y: 0,
            transition: {
                type: 'string',
                damping:12,
                stiffness:100,
            }
        },
        hidden: {
            opacity: 0,
            y:20,
        },
    }

    return (
        <motion.div className={style.Wrapper}
                    variants={containerAnimation}
                    i={1}
                    initial="hidden"
                    animate={'visible'}
                    whileInView="visible"
                    custom={2}
                    viewport={{amount: 0.13}}
        >
            <h1 className={style.Title}>
                {text.split(" ").map((word, index) => {
                    return (
                        <motion.span style={{marginRight: '5px'}} key={index}>
                            {word}
                        </motion.span>
                    )
                })}
            </h1>
        </motion.div>
    )
}
