import {motion} from 'framer-motion';
import style from './animated.module.css';

export const AnimatedTitle = () => {
    const text = 'Animated Text' // This would normally be passed into this component as a prop!

    // const ctrls = useAnimation();
    //
    // const {ref, inView} = useInView({
    //     threshold: 0.5,
    //     triggerOnce: true,
    // });

    // useEffect(() => {
    //     if (inView) {
    //         ctrls.start("visible");
    //     }
    //     if (!inView) {
    //         ctrls.start("hidden");
    //     }
    // }, [ctrls, inView]);
    //
    const wordAnimation = {
        hidden: {
            opacity: 1,
        },
        visible: {
            opacity: 1,
        },
    };

    const characterAnimation = {
        hidden: {
            opacity: 0,
            y: `0.25em`,
        },
        visible: {
            opacity: 1,
            y: `0em`,
            transition: {
                duration: 1,
                ease: [0.2, 0.65, 0.3, 0.9],
            },
        },
    };

    return (
        <motion.h2 aria-label={text} role="heading" className={style.Title}>
            {text.split(" ").map((word, index) => {
                return (
                    <motion.span
                        aria-hidden="true"
                        key={index}
                        initial="hidden"
                        variants={wordAnimation}
                        transition={{
                            delayChildren: index * 0.25,
                            staggerChildren: 0.05,
                        }}
                    >
                        {word.split("").map((character, index) => {
                            return (
                                <motion.span
                                    aria-hidden="true"
                                    key={index}
                                    variants={characterAnimation}
                                >
                                    {character}
                                </motion.span>
                            );
                        })}
                    </motion.span>
                );
            })}
        </motion.h2>
    )
}
